import React, {useState, useEffect} from 'react';
import {createBottomTabNavigator} from '@react-navigation/bottom-tabs';
import Icon from 'react-native-vector-icons/Ionicons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {Alert} from 'react-native';

import DashboardScreen from '../screens/DashboardScreen';
import LedgerScreen from '../screens/LedgerScreen';
import MoreScreen from '../screens/MoreScreen';
import Header from '../components/Header';
import {colors, fontSize} from '../utils/theme';

const Tab = createBottomTabNavigator();

const MainTabNavigator = ({route}) => {
  const [user, setUser] = useState(route.params?.user || null);

  useEffect(() => {
    if (!user) {
      loadUserData();
    }
  }, [user]);

  const loadUserData = async () => {
    try {
      const userData = await AsyncStorage.getItem('user');
      if (userData) {
        setUser(JSON.parse(userData));
      }
    } catch (error) {
      console.error('Error loading user data:', error);
    }
  };

  const handleNotificationPress = () => {
    Alert.alert('Notifications', 'No new notifications');
  };

  return (
    <>
      <Header user={user} onNotificationPress={handleNotificationPress} />
      <Tab.Navigator
        screenOptions={{
          headerShown: false,
          tabBarActiveTintColor: colors.tabBarActive,
          tabBarInactiveTintColor: colors.tabBarInactive,
          tabBarStyle: {
            backgroundColor: colors.tabBarBg,
            borderTopWidth: 1,
            borderTopColor: colors.border,
            height: 60,
            paddingBottom: 8,
            paddingTop: 8,
          },
          tabBarLabelStyle: {
            fontSize: fontSize.small,
            fontWeight: '500',
          },
        }}>
        <Tab.Screen
          name="Dashboard"
          component={DashboardScreen}
          initialParams={{user}}
          options={{
            tabBarIcon: ({color, size}) => (
              <Icon name="home" size={size} color={color} />
            ),
          }}
        />
        <Tab.Screen
          name="Ledger"
          component={LedgerScreen}
          options={{
            tabBarIcon: ({color, size}) => (
              <Icon name="book" size={size} color={color} />
            ),
          }}
        />
        <Tab.Screen
          name="More"
          component={MoreScreen}
          initialParams={{user}}
          options={{
            tabBarIcon: ({color, size}) => (
              <Icon name="grid" size={size} color={color} />
            ),
          }}
        />
      </Tab.Navigator>
    </>
  );
};

export default MainTabNavigator;

import React, {useState, useEffect} from 'react';
import {View, Text, StyleSheet, ScrollView, TouchableOpacity} from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';
import {colors, spacing, fontSize, fontWeight} from '../utils/theme';
import AddAccountModal from '../components/AddAccountModal';
import {initAccountsDatabase, getAllAccounts} from '../services/accountsDatabase';
import {initTransactionsDatabase, calculateAccountBalance} from '../services/transactionsDatabase';

const DashboardScreen = ({route, navigation}) => {
  const {user} = route.params || {};
  const [selectedPeriod, setSelectedPeriod] = useState('1month');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [selectedYear, setSelectedYear] = useState('');
  const [showAddAccountModal, setShowAddAccountModal] = useState(false);
  const [accounts, setAccounts] = useState([]);

  // Initialize database and load accounts on mount
  useEffect(() => {
    try {
      initAccountsDatabase();
      initTransactionsDatabase();
      loadAccounts();
    } catch (error) {
      console.error('Failed to initialize accounts database:', error);
    }
  }, []);

  // Refresh accounts when screen comes into focus
  useEffect(() => {
    const unsubscribe = navigation.addListener('focus', () => {
      loadAccounts();
    });

    return unsubscribe;
  }, [navigation]);

  // Load accounts from database with updated balances
  const loadAccounts = () => {
    try {
      const accountsList = getAllAccounts();

      // Update each account with current balance from transactions
      const accountsWithBalance = accountsList.map(account => {
        const balance = calculateAccountBalance(account.id);
        return {
          ...account,
          balance: balance,
        };
      });

      console.log('Loaded accounts with balances:', accountsWithBalance);
      setAccounts(accountsWithBalance);
    } catch (error) {
      console.error('Failed to load accounts:', error);
    }
  };

  // Fake data
  const metrics = {
    billed: 150000,
    collected: 120000,
    due: 30000,
    expenses: 45000,
    margin: 75000,
    services: 25000,
    income: 105000,
    totalStock: 45,
    pendingOrders: 8,
  };

  const formatCurrency = amount => {
    return `₹${amount.toLocaleString('en-IN')}`;
  };

  return (
    <View style={styles.container}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Revenue Period Selector */}
        <View style={styles.periodSelector}>
          <View style={styles.periodHeader}>
            <Icon name="calendar-outline" size={16} color={colors.primary} />
            <Text style={styles.periodHeaderText}>Revenue Period</Text>
          </View>
          <View style={styles.dropdownGrid}>
            <View style={styles.dropdownContainer}>
              <Text style={styles.dropdownLabel}>Quick Period</Text>
              <View style={styles.dropdown}>
                <Text style={styles.dropdownText}>1 Month</Text>
              </View>
            </View>
            <View style={styles.dropdownContainer}>
              <Text style={styles.dropdownLabel}>Month</Text>
              <View style={styles.dropdown}>
                <Text style={styles.dropdownText}>Select</Text>
              </View>
            </View>
            <View style={styles.dropdownContainer}>
              <Text style={styles.dropdownLabel}>Year</Text>
              <View style={styles.dropdown}>
                <Text style={styles.dropdownText}>Select</Text>
              </View>
            </View>
          </View>
        </View>

        {/* Financial Metrics Grid */}
        <View style={styles.metricsGrid}>
          {/* Billed */}
          <View style={styles.metricCard}>
            <View style={styles.metricHeader}>
              <View style={[styles.metricIcon, {backgroundColor: '#E0E7FF'}]}>
                <Icon name="document-text" size={14} color="#4F46E5" />
              </View>
              <Text style={styles.metricLabel}>Billed</Text>
            </View>
            <Text style={styles.metricValue}>{formatCurrency(metrics.billed)}</Text>
          </View>

          {/* Collected */}
          <View style={styles.metricCard}>
            <View style={styles.metricHeader}>
              <View style={[styles.metricIcon, {backgroundColor: '#D1FAE5'}]}>
                <Icon name="cash" size={14} color="#10B981" />
              </View>
              <Text style={styles.metricLabel}>Collected</Text>
            </View>
            <Text style={[styles.metricValue, {color: '#10B981'}]}>
              {formatCurrency(metrics.collected)}
            </Text>
          </View>

          {/* Due */}
          <View style={styles.metricCard}>
            <View style={styles.metricHeader}>
              <View style={[styles.metricIcon, {backgroundColor: '#FEE2E2'}]}>
                <Icon name="time" size={14} color="#EF4444" />
              </View>
              <Text style={styles.metricLabel}>Due</Text>
            </View>
            <Text style={[styles.metricValue, {color: '#EF4444'}]}>
              {formatCurrency(metrics.due)}
            </Text>
          </View>

          {/* Expenses */}
          <View style={styles.metricCard}>
            <View style={styles.metricHeader}>
              <View style={[styles.metricIcon, {backgroundColor: '#F3F4F6'}]}>
                <Icon name="card" size={14} color="#6B7280" />
              </View>
              <Text style={styles.metricLabel}>Expenses</Text>
            </View>
            <Text style={styles.metricValue}>{formatCurrency(metrics.expenses)}</Text>
          </View>
        </View>

        {/* Quick Actions Card */}
        <View style={styles.balanceCard}>
          {/* Quick Action Boxes - Google Pay Style */}
          <View style={styles.quickActionsGrid}>
            <TouchableOpacity
              style={styles.quickActionItem}
              onPress={() => setShowAddAccountModal(true)}>
              <View style={styles.quickActionIcon}>
                <Icon name="person-add" size={20} color="#6366F1" />
              </View>
              <Text style={styles.quickActionText}>Add{'
'}Account</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.quickActionItem}>
              <View style={styles.quickActionIcon}>
                <Icon name="send" size={20} color="#10B981" />
              </View>
              <Text style={styles.quickActionText}>Send{'
'}Amount</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.quickActionItem}>
              <View style={styles.quickActionIcon}>
                <Icon name="cash" size={20} color="#F59E0B" />
              </View>
              <Text style={styles.quickActionText}>Request{'
'}Money</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.quickActionItem}>
              <View style={styles.quickActionIcon}>
                <Icon name="swap-horizontal" size={20} color="#8B5CF6" />
              </View>
              <Text style={styles.quickActionText}>Transfer{'
'}Funds</Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* My Accounts */}
        <View style={styles.accountsSection}>
          <Text style={styles.sectionTitle}>My Accounts</Text>
          {accounts.length > 0 ? (
            <View style={styles.accountsList}>
              {accounts.map(account => (
                <TouchableOpacity
                  key={account.id}
                  style={styles.accountItem}
                  onPress={() => {
                    // Navigate to different screens based on account type
                    if (account.account_type === 'earning') {
                      navigation.navigate('AccountDetail', {account});
                    } else {
                      navigation.navigate('LiabilityAccountDetail', {account});
                    }
                  }}>
                  {/* Account Icon */}
                  <View
                    style={[
                      styles.accountIcon,
                      {
                        backgroundColor:
                          account.account_type === 'earning'
                            ? '#D1FAE5'
                            : '#FEF3C7',
                      },
                    ]}>
                    <Icon
                      name={
                        account.account_type === 'earning'
                          ? 'trending-up'
                          : 'wallet'
                      }
                      size={22}
                      color={
                        account.account_type === 'earning'
                          ? '#10B981'
                          : '#F59E0B'
                      }
                    />
                  </View>

                  {/* Account Details */}
                  <View style={styles.accountDetails}>
                    <View style={styles.accountHeader}>
                      <Text style={styles.accountName}>
                        {account.account_name}
                      </Text>
                      <View style={styles.badgeContainer}>
                        <View
                          style={[
                            styles.accountBadge,
                            {
                              backgroundColor:
                                account.account_type === 'earning'
                                  ? '#D1FAE5'
                                  : '#FEF3C7',
                            },
                          ]}>
                          <Text
                            style={[
                              styles.accountBadgeText,
                              {
                                color:
                                  account.account_type === 'earning'
                                    ? '#10B981'
                                    : '#F59E0B',
                              },
                            ]}>
                            {account.account_type === 'earning'
                              ? 'Earning'
                              : 'Liability'}
                          </Text>
                        </View>
                        {account.is_primary === 1 && (
                          <View style={styles.primaryBadge}>
                            <Icon name="star" size={10} color="#F59E0B" />
                            <Text style={styles.primaryBadgeText}>PRIMARY</Text>
                          </View>
                        )}
                      </View>
                    </View>
                    <Text style={styles.accountBalance}>
                      {formatCurrency(account.balance || 0)}
                    </Text>
                    <Text style={styles.accountDate}>
                      Created on{' '}
                      {new Date(account.created_at).toLocaleDateString('en-IN', {
                        day: 'numeric',
                        month: 'short',
                        year: 'numeric',
                      })}
                    </Text>
                  </View>
                </TouchableOpacity>
              ))}
            </View>
          ) : (
            <View style={styles.emptyAccounts}>
              <Icon name="wallet-outline" size={48} color="#D1D5DB" />
              <Text style={styles.emptyText}>No accounts yet</Text>
              <Text style={styles.emptySubtext}>
                Create your first account to get started
              </Text>
            </View>
          )}
        </View>
      </ScrollView>

      {/* Add Account Modal */}
      <AddAccountModal
        visible={showAddAccountModal}
        onClose={() => setShowAddAccountModal(false)}
        onSuccess={() => {
          // Refresh accounts list
          console.log('Account added successfully - refreshing list');
          loadAccounts();
        }}
      />
    </View>
  );
};

