import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { checkUsername, completeSetup } from '../services/api';
import { saveUserData } from '../services/database';
import AsyncStorage from '@react-native-async-storage/async-storage';

const SetupScreen = ({ route, navigation }) => {
  const { firebaseUid, email, displayName, photoURL } = route.params;

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [checkingUsername, setCheckingUsername] = useState(false);
  const [usernameAvailable, setUsernameAvailable] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  // Validate username format
  const validateUsernameFormat = (text) => {
    const regex = /^[a-zA-Z0-9._-]+$/;
    return regex.test(text);
  };

  // Check username availability (debounced)
  useEffect(() => {
    if (username.length < 3) {
      setUsernameAvailable(null);
      setSuggestions([]);
      return;
    }

    if (!validateUsernameFormat(username)) {
      setUsernameAvailable(false);
      setSuggestions([]);
      return;
    }

    const timeoutId = setTimeout(async () => {
      setCheckingUsername(true);
      try {
        const response = await checkUsername(username);
        setUsernameAvailable(response.available);
        setSuggestions(response.suggestions || []);
      } catch (error) {
        console.error('Username check error:', error);
      } finally {
        setCheckingUsername(false);
      }
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [username]);

  // Handle setup completion
  const handleCompleteSetup = async () => {
    // Validations
    if (!username.trim()) {
      Alert.alert('Error', 'Please enter a username');
      return;
    }

    if (!validateUsernameFormat(username)) {
      Alert.alert(
        'Invalid Username',
        'Username can only contain letters, numbers, dots, hyphens, and underscores',
      );
      return;
    }

    if (usernameAvailable === false) {
      Alert.alert('Error', 'Username is already taken. Please choose another.');
      return;
    }

    if (!password.trim()) {
      Alert.alert('Error', 'Please enter a password');
      return;
    }

    if (password.length < 6) {
      Alert.alert('Error', 'Password must be at least 6 characters');
      return;
    }

    if (password !== confirmPassword) {
      Alert.alert('Error', 'Passwords do not match');
      return;
    }

    setLoading(true);
    try {
      const response = await completeSetup(firebaseUid, username, password);

      if (response.success) {
        // Save user data locally
        saveUserData(response.user);
        await AsyncStorage.setItem('user', JSON.stringify(response.user));

        Alert.alert('Success', 'Account setup completed!', [
          {
            text: 'OK',
            onPress: () => navigation.replace('Home', { user: response.user }),
          },
        ]);
      }
    } catch (error) {
      console.error('Setup error:', error);
      Alert.alert('Error', error.message || 'Failed to complete setup');

      // If username taken, show suggestions
      if (error.suggestions) {
        setSuggestions(error.suggestions);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle suggestion press
  const handleSuggestionPress = (suggested) => {
    setUsername(suggested);
  };

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={styles.container}>
      <ScrollView
        contentContainerStyle={styles.scrollContainer}
        keyboardShouldPersistTaps="handled">
        <View style={styles.innerContainer}>
          <Text style={styles.title}>Create Your Account</Text>
          <Text style={styles.subtitle}>Welcome, {displayName}!</Text>

          <View style={styles.formContainer}>
            {/* Username Input */}
            <View style={styles.inputContainer}>
              <Text style={styles.label}>Choose Username</Text>
              <TextInput
                style={styles.input}
                placeholder="username"
                value={username}
                onChangeText={setUsername}
                autoCapitalize="none"
                editable={!loading}
              />

              {checkingUsername && (
                <Text style={styles.checkingText}>Checking...</Text>
              )}

              {!checkingUsername && usernameAvailable === true && (
                <Text style={styles.availableText}>✓ Available</Text>
              )}

              {!checkingUsername &&
                usernameAvailable === false &&
                username.length >= 3 && (
                  <Text style={styles.takenText}>✗ Already taken</Text>
                )}

              {!validateUsernameFormat(username) && username.length > 0 && (
                <Text style={styles.errorText}>
                  Only letters, numbers, dots, hyphens, and underscores allowed
                </Text>
              )}
            </View>

            {/* Username Suggestions */}
            {suggestions.length > 0 && (
              <View style={styles.suggestionsContainer}>
                <Text style={styles.suggestionsTitle}>Suggestions:</Text>
                {suggestions.map((suggestion, index) => (
                  <TouchableOpacity
                    key={index}
                    style={styles.suggestionButton}
                    onPress={() => handleSuggestionPress(suggestion)}>
                    <Text style={styles.suggestionText}>{suggestion}</Text>
                    <Text style={styles.useText}>Use</Text>
                  </TouchableOpacity>
                ))}
              </View>
            )}

            {/* Password Input */}
            <View style={styles.inputContainer}>
              <Text style={styles.label}>Create Password</Text>
              <TextInput
                style={styles.input}
                placeholder="Password (min 6 characters)"
                value={password}
                onChangeText={setPassword}
                secureTextEntry
                editable={!loading}
              />
            </View>

            {/* Confirm Password Input */}
            <View style={styles.inputContainer}>
              <Text style={styles.label}>Confirm Password</Text>
              <TextInput
                style={styles.input}
                placeholder="Confirm Password"
                value={confirmPassword}
                onChangeText={setConfirmPassword}
                secureTextEntry
                editable={!loading}
              />
            </View>

            {/* Google Account Info */}
            <View style={styles.googleInfo}>
              <Text style={styles.googleInfoTitle}>Google Account:</Text>
              <Text style={styles.googleInfoText}>{email} ✓</Text>
              <Text style={styles.googleInfoSubtext}>
                Your data will be backed up to Google Drive
              </Text>
            </View>

            {/* Complete Setup Button */}
            <TouchableOpacity
              style={[styles.button, loading && styles.buttonDisabled]}
              onPress={handleCompleteSetup}
              disabled={loading || checkingUsername || usernameAvailable === false}>
              {loading ? (
                <ActivityIndicator color="#fff" />
              ) : (
                <Text style={styles.buttonText}>Complete Setup</Text>
              )}
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

